import React from 'react'
import Build from '../../assets/build.webp'
import styled from 'styled-components'

const SubContainer = styled.div`
    display: flex;
    padding: 2em 4em;
`

const Title = styled.h2`
    font-size: 4.6em;
    font-family: Ruberoid;
    letter-spacing: .2px;
    line-height: 1em;
    margin-bottom: 0;
`

const Description = styled.p`
    margin-right: 10em;
`

function Landing2() {
  return (
    <section>
        <SubContainer>
            <div>
                <Title>Build whatever</Title>
                <Description>Deploy fast. Launch a blockchain with leading Ethereum rollup frameworks or transform nearly any VM into your own sovereign chain. <br/>With Celestia underneath, a customizable blockchain becomes as easy to deploy as a smart contract.</Description>
                <button>Build Modular</button>
                <button>Deploy</button>
            </div>

            <img src={Build} alt='Build'/>
        </SubContainer>

        {/* <div>
            <h2>Build whatever</h2>
            <p>Deploy fast. Launch a blockchain with leading Ethereum rollup frameworks or transform nearly any VM into your own sovereign chain. <br/>With Celestia underneath, a customizable blockchain becomes as easy to deploy as a smart contract.</p>
            <button>Learn Celestia</button>
        </div> */}
    </section>
  )
}

export default Landing2
