import React from 'react'
import styled from 'styled-components'
import LazyBridging from '../../assets/Celestia_Lazybridging.webp'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faArrowRight } from '@fortawesome/free-solid-svg-icons'
import CelestiaBlogs from '../../celestiaBlogs.js'

const Container = styled.div`
    padding: 0 4em;
    padding-bottom: 3em;

    h2{
        font-size: 4.4em;
        font-family: Ruberoid;
        letter-spacing: .2px;
        line-height: 1em;
        margin: 0;
        padding: 0;
    }
`

const ArrowSpan = styled(FontAwesomeIcon)`
    color: #7b2bf9;
    transform: rotate(-45deg);
`

const MainBlog = styled.a`
    min-width: 55%;

    &:hover{
        p{
            color: #7b2bf9;
        }

        .hoverIcon{
            transform: rotate(0deg);
        }
    }
`

const Read = styled.div`

    width: 80%;

    p{
        font-family: Ruberoid;
        font-weight: 600;
        font-size: 1em;
        padding: .5em 0;   
    }


    &:hover{
        p{
            color: #7b2bf9;
        }

        .hoverIcon{
            transform: rotate(0deg);
        }
    }
`

const FlexContainer = styled.div`
    display: flex;
    gap: 5em;   
    margin-top: 3em;
    font-family: Ruberoid;

    img{
        width: 100%;
        border-radius: 5px;
    }

    a{
        text-decoration: none;
        color: inherit;
    }

    h3{
        font-size: 2em;
        margin: 0;
    }
`

const Blog = styled.p`
    font-size: .6em;
    font-weight: bold;
`

const MoreBlogs = styled.div`
    display: flex;
    flex-direction: column;
    align-items: center;
    gap: 2em;
    width: 100%; 
`

const MoreBlog = styled.a`
    display: flex;
`

const BlogImage = styled.img`
    width: 30%;
`

function Landing5() {
  return (
    <Container>
        <h2>Blog</h2>
        <FlexContainer>
            <MainBlog href='https://blog.celestia.org/lazybridging/' target='_blank' >
                <img src={LazyBridging} alt='Lazy Bridging Blog Heading'/>
                <Blog>BLOG</Blog>
                <h3>
                    Lazybridging: the Celestia endgame to solve 
                    fragmentation and stream assets from anywhere
                </h3>
                <Read><p>Read now <ArrowSpan icon={faArrowRight} className='hoverIcon'/></p></Read>
            </MainBlog>
            <MoreBlogs>
                {
                    CelestiaBlogs.map((blog) => {
                        return(
                            <MoreBlog href={blog.link} target='_blank' >
                                <BlogImage src={blog.image} alt={blog.alt}/>
                                <div>
                                    {blog.title}
                                </div>

                            </MoreBlog>
                        )
                    })
                }
            </MoreBlogs>
        </FlexContainer>
    </Container>
  )
}

export default Landing5
