import React from 'react'
import styled from 'styled-components'
import NodeBG from '../../assets/nodeBG.webp'

const Container = styled.div`

  position: relative;
  padding: 5em;
  padding-top: 15em;  
  padding-right: 35em;

  &:before {
    background: url(${NodeBG}) no-repeat;
    background-size: 55%;
    background-position: right;
    content: "";
    height: 100%;
    left: 0;
    position: absolute;
    top: 0;
    width: 100%;
    z-index: -1;
  } 

  h2{

    font-size: 1em;
  }
`

const Title = styled.h1`
    font-size: 7em;
    margin: 0;
    padding: 0;
    font-family: "Ruberoid";
    line-height: 1.05;
`

const SubTitle = styled.p`
    padding-right: 23em;
    font-family: "Ruberoid";
    font-weight: 500;
    font-size: 1.2em;
    word-spacing: 2px;
    line-height: 1.5;
`

function Node1() {
  return (
    <Container>
        <Title>Directly verify</Title>
        <SubTitle>Join the network or integrate the Celestia light node.</SubTitle>
        <div>
            <button>Run a node</button>
            <button>Integrate</button>
        </div>
    </Container>
  )
}

export default Node1
