import React from 'react'
import socials from '../../Socials'
import styled from 'styled-components'
import ModularSummit from '../../assets/modularSummit.webp'
import ModularMeetup from '../../assets/modularMeetup.webp'

const Container = styled.section`
    padding: 0 4em;
    padding-bottom: 3em;

    h2{
        font-size: 4.4em;
        font-family: Ruberoid;
        letter-spacing: .2px;
        line-height: 1em;
        margin: 0;
        padding: 0;
    }

    p{
        font-weight: 200;
        letter-spacing: .2px;
        font-size: 1.02em;
    }
`

const Slider = styled.div`
    width: 90%;
    display: flex;
    gap: 1em;
    padding-top: 4em;
    overflow-x: scroll;
    overflow-y: hidden;
    height: 30em;
`

const SocialsContainer = styled.div`
    display: flex;
    flex-wrap: wrap; 
    min-width: 45%;   
    border: 1px solid #d9d5df;
    border-radius: 6px;
    padding: 2em;
    gap: 4em;
`

const SocialContainer = styled.div`
    display: flex;
    gap: 1.5em;
    width: 40%;

    h3{
        font-family: Ruberoid
    }

    img{
        width: 50%;
        height: 50%;
    }
`

const ImageContainer = styled.div`
    display: flex;
    position: relative;

    img{
        height: 100%;
        margin: 0;
    }

    p{
        position: absolute;
        top: 10%;
    }
`

function Landing4() {
  return (
    <Container>
        <h2>Join the community</h2>
        <p>Join the Celestia community online or hang out at one of the grassroots Modular Meetups</p>
        <Slider>
            <SocialsContainer>
                {
                    socials.map((social) => {
                        return(
                        <SocialContainer>
                            <img src={social.icon} alt={social.logo}/>
                            <div>
                                <h3>{social.social}</h3>
                                <p>{social.slogan}</p>
                            </div>
                        </SocialContainer>
                        )
                    })
                }
            </SocialsContainer>
            <ImageContainer>
                <div>
                    <p>Modular Summit 2023</p>
                    <img src={ModularSummit} alt='Modular Summit'/>
                </div>
                <div>
                    <p>Modular Meetup</p>
                    <img src={ModularMeetup} alt='Modular Meetup'/>
                </div>
            </ImageContainer>
        </Slider>
    </Container>
  )
}

export default Landing4
