import React from 'react';
import styled from 'styled-components';
import CelestiaLogo from '../assets/CelestiaLogo.svg';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import '../assets/fonts/stylesheet.css'
import { faAngleUp, faAngleDown } from '@fortawesome/free-solid-svg-icons';

const Container = styled.header`
    display: flex; 
    padding: 2em 4.3em;
    align-items: center;
    justify-content: space-between;
`

const List = styled.ul`
    list-style: none;
    display: flex;
    gap: 1.3em;
    font-family: 'Ruberoid';
    font-weight: 500;
    font-size: 1.15em;
`

const Arrow = styled(FontAwesomeIcon)`
    padding: 0;
`

function Heading() {
  return (
    <Container>
        <img src={CelestiaLogo} alt='Celestia Logo'/>
        <nav>
            <List>
                <li>Learn <Arrow icon={faAngleDown}/></li>
                <li>Build <FontAwesomeIcon icon={faAngleDown}/></li>
                <li>Community <FontAwesomeIcon icon={faAngleDown}/></li>
            </List>
        </nav>
    </Container>
  )
}

export default Heading
