import React from 'react'
import Resources from '../../Resources'
import styled from 'styled-components'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faArrowRight } from '@fortawesome/free-solid-svg-icons'

const BuildCard = styled.div`
    background: hsla(0,0%,100%,.5);
    backdrop-filter: blur(1.2em);
    border: 1px solid hsla(0,0%,47%,.15);
    border-radius: 8px;
    padding: 2em 2.3em;
    line-height: 1;
    width: 30%; 

    h3{
        padding: 0;
        margin: 0;
        font-family: Ruberoid;
        font-size: 1.2em;
        margin-top: 1em;
    }

    p{
        font-weight: 200;
        font-size: .8em;
        padding-top: .3em;
        margin-bottom: 1.5em;
    }

    .arrow{
        color: #7b2bf9;

    }

    a{
        text-decoration: none;
        color: inherit;
        pointer: cursor;
        font-family: Ruberoid;
        font-weight: bold;
        font-size: .9em;
        padding-top: .8em;
        display: flex;
        width: max-content;
        gap: .3em;

        &:hover{
            gap: 1em;
            transition: all .2s ease-in-out;
        }

    }
`

const Title = styled.h2`
    font-size: 4.8em;
    margin: 1em 0;
    text-align: center;
    padding: 0;
    font-family: Ruberoid;
`

const Container = styled.section`
    margin-bottom: 30em;
`

const ResourceContainer = styled.div`
    padding: 0 4.5em;
    display: flex;
    gap: 1em;
`

function Build3() {
    const Resource2 = [
        {
            title: "Blobstream",
            sub: 'Use Celestia as the DA layer for your Ethereum L2.',
            button: 'Blobstream documentation'
        },

        {
            title: "Node API",
            sub: 'Use the celestia-node API to publish and retrieve transactions from Celestia.',
            button: 'Node API Documentation'
        }
    ]

  return (
    <Container>
        <Title>Developer resources</Title>
        <ResourceContainer>
            {
                Resources.map((resource) => {
                    return(
                        <BuildCard>
                            <img src={resource.image} alt={resource.title}/>
                            <h3>{resource.title}</h3>
                            <p>{resource.sub}</p>
                            <a href='#' target='_blank'><span>{resource.button}</span> <FontAwesomeIcon icon={faArrowRight} className='arrow'/></a>
                        </BuildCard>
                    )
                })
            }
        </ResourceContainer>
        <div>   
            {Resource2.map(() => {

            })}
        </div>  
    </Container>
  )
}

export default Build3
