import React from "react";
import styled from "styled-components";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import Navigate from '../navigate';

const Lists = styled.ul`
    list-style: none;
    display: flex;
    gap: 1.3em;
    font-family: 'Ruberoid';
    font-weight: 500;
    font-size: ${({scrolled}) => (scrolled ? '.9em' : '1.15em')};
    transition: all 0.6s ease-in-out;
`

const List = styled.li`
    cursor: pointer;
`

const Arrow = styled(FontAwesomeIcon)`
    padding: 0;
    transition: transform 0.4s ease-in-out;
    transform: ${({hover}) => (hover ? 'rotate(180deg)' : 'rotate(0deg)')}
`

const SubList = styled.div`
    display: ${({show}) => (show ? 'block' : 'none')};
    position: fixed;
    width: 25%;
    top: ${({scrolled}) => (scrolled ? '70%' : '13%')};
    left: 68%;
    font-size: 1.15em;
    font-size: ${({scrolled}) => (scrolled ? '1.5em' : '1.15em')};
    text-wrap: no-wrap;
    margin-left: 1em;
    padding: 1em;
    background: rgba(250, 250, 250, 0.68);
    backdrop-filter: blur(.5em);
    border-radius: 10px;
`

const SubContainer = styled.div`
    border: none;
    width: 90%;
    display: flex;
    gap: 1.5em;
    align-items: center;
    margin: 0 .5em;
    padding: .5em 0;



    &:not(:last-child){
        border-bottom: .1px solid #d9d5df;
    }

    img{
        background: #000;
        padding: .1em;
        border-radius: 50%;
        width: 7%;
        height: 50%;
    }

    &:hover{
        img{
            background-color: #7b2bf9;
        }

        .subtitle{
            color: #7b2bf9;
        }
    }
`

const SubTitles = styled.div`
    p{
        margin: 0;
        padding: 0;
        padding-bottom: .2em;
    }



    display: flex;
    flex-direction: column;
`

const Title = styled.p`
    color: #777;
    font-size: .6em;
`

const SubTitle = styled.p`
    color: #000;
    font-size: .8em;
    font-weight: bold;
`

function Navigation({nav, index, handleHover, handleNoHover, faAngleUp, faAngleDown}) {
    const [hover, setHover] = useState(null);
    const [scrolled, setScrolled] = useState(false);

    function handleHover(index){
        setHover(index)
    }

    function handleNoHover(){
        setHover(null)
    }

    useEffect(() => {
        function handleScroll(){
            if(window.scrollY > 160){

                setScrolled(true)
            }else{
                setScrolled(false)
            }
        }

        window.addEventListener('scroll', handleScroll)

        return() => {
            window.removeEventListener('scroll', handleScroll)
        }
    }, [])

    return (<nav>
        <Lists scrolled={scrolled}>
            {Navigate.map((nav, index) => {
            return(
                <List key={index} onMouseEnter={() => handleHover(index)} onMouseLeave={handleNoHover}>
                    {nav.nav} 
                    <Arrow hover={hover === index} icon={hover === index ? faAngleUp : faAngleDown}/>
                    <SubList 
                        show={hover === index} 
                        scrolled={scrolled} 
                        onMouseEnter={() => handleHover(index)} 
                        onMouseLeave={handleNoHover}
                    >
                        {nav.subNav.map((sub, index) => {
                        return(
                            <SubContainer key={index}>
                                <img src={sub.icon} alt={sub.alt} />
                                <SubTitles>
                                    <Title>{sub.title}</Title>
                                    <SubTitle className='subtitle'>{sub.subTitle}</SubTitle>
                                </SubTitles>
                            </SubContainer>)})}
                    </SubList>
                </List>)
})}
        </Lists>
    </nav>);
}

export default Navigation
