import { faArrowUp } from '@fortawesome/free-solid-svg-icons'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import React from 'react'
import styled from 'styled-components'

const Container = styled.div`
    display: flex;
    gap: 2em;
    justify-content: space-between;
    padding: 5em;
    background-color: #000;

    h3{
        font-size: 2.5em;
        font-weight: 400;
        font-family: Ruberoid;
        padding: 0;
        color: #fff;
    }

    li{
        border-top: 1.5px solid black;
        padding: 3em 0;
        font-size: .9em;
        color: #fff;
    }
`

const Sub = styled(FontAwesomeIcon)`
  padding-right: 1em;
  padding-left: 1em;
  transform: rotate(45deg);
`

const Button = styled.button`
  border-radius: 50em;
  border: none;
  font-size: .8em;
  font-family: "Ruberoid";
  font-weight: 500;
  cursor: pointer;
  width: max-content;
  text-align: center;
  padding: 1em;
  background-color: #7b2bf9;
  color: white;

  &:hover {
    background-color: #000;
    color: #ffffff;
  }

  @media (min-width: 768px) {
    width: auto;
    padding: .6em 1.3em;
  }
`

function Careers3() {
  return (
    <Container>
        <div>
            <h3>Perks</h3>
            <Button>CURRENT OPENINGS <Sub icon={faArrowUp} className="arrow" /></Button>
        </div>

        <ul>
            <li>Employment benefits</li>
            <li>4 weeks annual vacation</li>
            <li>Meaningful long-term compensation package</li>
            <li>Flexible and remote work environment</li>
        </ul>
    </Container>
  )
}

export default Careers3
