import React, { useState } from 'react'
import Framework from '../../Framework'
import styled from 'styled-components'

const Container = styled.section`
    width: 90%;
    display: flex;
    flex-direction: column;
    align-items: center;
    margin: 0 auto;
    margin-top: 7em;
    margin-bottom: 30em;
`

const Title = styled.h2`
    font-size: 4.8em;
    margin: 0;
    padding: 0;
    font-family: Ruberoid;
`

const SubTitle = styled.p`
    margin: 0;
    padding: 0;
    font-weight: 300;
`

const ButtonContainer = styled.div`
    margin: 2em;
    display: flex;
    gap: 1em;

    button{
        padding: .5em 2em;
        border-radius: 5px;
        border: none;

    }
`

function Build2() {
    const [selectedFramework, setSelectedFramework] = useState('ALL')

    function handleSelectFramework(button){
        setSelectedFramework(button)
    }

  return (
    <Container>
        <Title>Choose a framework</Title>
        <SubTitle>Get started quickly by using Celestia with leading rollup frameworks.</SubTitle>
        <ButtonContainer>
            {
                ['ALL', 'ETHEREUM', 'SOVEREIGN'].map((button) => {
                    return(<button onClick={() => handleSelectFramework(button)}>{button}</button>)
                })
            }
        </ButtonContainer>


        {
            Framework.map((framework) => {

                if(selectedFramework === 'ALL'){
                    return(<div>{framework.title}</div>)
                }

                if(selectedFramework === 'ETHEREUM' || 'SOVEREIGN'){
                    return(framework.stack === selectedFramework && <div>{framework.title}</div>)
                } 


        })}
    </Container>
  )
}

export default Build2
