import React from 'react';
import styled from 'styled-components';
import CelestiaLogo from '../assets/CelestiaLogo.svg';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import '../index.css';
import { faAngleUp, faAngleDown } from '@fortawesome/free-solid-svg-icons';

const Container = styled.header`
    display: flex; 
    padding: 1em;
    align-items: center;
    justify-content: space-between;
`

const Logo = styled.img`

`

const Navigation = styled.nav`

`

const List = styled.ul`
    list-style: none;
    display: flex;
    gap: 1em;
    font-family: 'Ruberoid';
    font-style: normal;
    font-weight: bold;
    font-size: 1.1em;
    color: #111;
    -webkit-text-stroke: .2px black;

`

const Arrow = styled(FontAwesomeIcon)`
    background: red;
    color: green;
    padding: 0;
`

function Heading() {
  return (
    <Container>
        <Logo src={CelestiaLogo} alt='Celestia Logo'/>
        <Navigation>
            <List>
                <li>Learn <Arrow icon={faAngleDown}/></li>
                <li>Build <FontAwesomeIcon icon={faAngleDown}/></li>
                <li>Community <FontAwesomeIcon icon={faAngleDown}/></li>
            </List>
        </Navigation>
    </Container>
  )
}

export default Heading
