import React, { useEffect, useState } from 'react';
import styled from 'styled-components';
import CelestiaLogo from '../assets/CelestiaLogo.svg';
import '../assets/fonts/stylesheet.css'
import { faAngleUp, faAngleDown } from '@fortawesome/free-solid-svg-icons';
import Navigation from './sub/Navigation';

const Container = styled.header`
    display: flex; 
    position: sticky;
    top: 0;
    left: 0;
    right: 0;
    z-index: 9999;
    align-items: center;
    justify-content: space-between;
    border: none;
    background-color: ${({scrolled}) => (scrolled ? 'rgba(255, 255, 255, 0.5)' : 'hsla(0,0%,100%,0)')};
    backdrop-filter: ${({scrolled}) => (scrolled ? 'blur(1em)' : 'none')}; 
    padding: ${({scrolled}) => (scrolled ? '1em 4.5em' : '2em 4.3em')};

    img{
        width: ${(props) => (props.scrolled ? '10%' : 'auto')};

        transition: transform 0.4s ease-in-out;
        transform: ${({scrolled}) => (scrolled ? 'scale(1.1, 1.1)' : 'scale(1, 1)')}
    }
    transition: all 0.6s ease-in-out;
`



function Heading() {
    const [scrolled, setScrolled] = useState(false);

  return (
    <Container scrolled={scrolled}>
        <img src={CelestiaLogo} alt='Celestia Logo'/>
        <Navigation scrolled={scrolled} nav={nav} index={index} handleHover={handleHover} handleNoHover={handleNoHover} hover={hover} faAngleUp={faAngleUp} faAngleDown={faAngleDown} sub={sub}  />
    </Container>
  )
}

export default Heading
