import React, { useState } from 'react';
import styled from 'styled-components';
import CelestiaLogo from '../assets/CelestiaLogo.svg';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import '../assets/fonts/stylesheet.css'
import { faAngleUp, faAngleDown } from '@fortawesome/free-solid-svg-icons';
import Navigate from '../navigate';

const Container = styled.header`
    display: flex; 
    padding: 2em 4.3em;
    align-items: center;
    justify-content: space-between;
    position: relative;
`

const Lists = styled.ul`
    list-style: none;
    display: flex;
    gap: 1.3em;
    font-family: 'Ruberoid';
    font-weight: 500;
    font-size: 1.15em;
`

const List = styled.li`
    cursor: pointer;
`

const Arrow = styled(FontAwesomeIcon)`
    padding: 0;
    transition: transform 0.4s ease-in-out;
    transform: ${({hover}) => (hover ? 'rotate(180deg)' : 'rotate(0deg)')}
`

const SubList = styled.div`
    display: ${({show}) => (show ? 'block' : 'none')};
    position: fixed;
    width: 28%;
    top: 15%;
    left: 65%;
    text-wrap: no-wrap;
    margin-left: 1em;
    padding: 1em;
    background: rgba(255, 255, 255, 0.5);
    backdrop-filter: blur(.5em);
    border-radius: 10px;
`

const SubContainer = styled.div`
    border: none;
    width: 100%;
    display: flex;
    gap: 1.5em;
    align-items: center;
    margin-left: .5em;
    padding: 1em 0;

    &:not(:last-child){
        border-bottom: 2px solid #000;
    }

    img{
        background: #000;
        padding: .1em;
        border-radius: 50%;
        width: 7%;
        height: 50%;
    }
`

const SubTitles = styled.div`
    p{
        margin: 0;
        padding: 0;
    }

    display: flex;
    flex-direction: column;

    &.title{
        order: 2;
        color: #000;
    }

    &.subtitle{
        order: 1;
        color: #777;
    }
`

function Heading() {

    const [hover, setHover] = useState(null)

    function handleHover(index){
        setHover(index)
    }

    function handleNoHover(){
        setHover(null)
    }

  return (
    <Container>
        <img src={CelestiaLogo} alt='Celestia Logo'/>
        <nav>
            <Lists>
                {
                    Navigate.map((nav, index) => {
                        return(
                            <List 
                                key={index} 
                                onMouseEnter={() => handleHover(index)} 
                                onMouseLeave={handleNoHover}
                            >
                                {nav.nav} <Arrow hover={hover === index} icon={hover === index ? faAngleUp : faAngleDown}/>
                                <SubList show={hover === index}>
                                    {nav.subNav.map((sub) => {
                                        return (
                                            <SubContainer>
                                                <img src={sub.icon} alt={sub.alt}/>
                                                <SubTitles>
                                                    <p className='title'>{sub.title}</p>
                                                    <p className='subtitle'>{sub.subTitle}</p>
                                                </SubTitles>
                                            </SubContainer>
                                        )
                                    })}
                                </SubList>
                            </List>
                        ) 
                    })
                }
            </Lists>
        </nav>
    </Container>
  )
}

export default Heading
