import React from 'react';
import styled from 'styled-components';
import BuildBG from '../../assets/BuildBG.webp';

const Container = styled.section`
    width: 100%;

    &:before{
    background-image: url(${BuildBG});
    background-size: cover;
    content: "";
    height: 100%;
    left: 0;
    position: absolute;
    top: 0;
    width: 100%;
    z-index: -1;
    padding-bottom: 10em;
    }
`

function Build1() {
    const build = [
        {
            title: "Build",
            sub: "Pick a rollup framework to begin building.",
            button: "Choose"
        },
        {
            title: "Integrate",
            sub: "Plug Celestia into your existing chain.",
            button: "Start"
        },
        {
            title: "Deploy",
            sub: "Explore Rollup-as-a-Service to deploy quickly",
            button: "Explore"
        }
    ]
  return (
    <Container>
        <h1>Build whatever</h1>

        <div>
            {
                build.map((build) => {
                    return(
                        <div>
                            <h3>{build.title}</h3>
                            <p>{build.sub}</p>
                            <p>{build.button}</p>
                        </div>
                    )
                })
            }

        </div>
    </Container>
  )
}

export default Build1
