import React, { Fragment } from 'react'
import styled from 'styled-components'
import Explore from '../../assets/explore.svg'
import Tia from '../../assets/tia.svg'
import Run from '../../assets/run.webp'
import ArrowImg from '../../assets/arrow.svg'

const Title = styled.h2`
    font-size: 4.4em;
    font-family: Ruberoid;
    letter-spacing: .2px;
    line-height: 1em;
    margin: 0;
    margin-bottom: 1.8em;
    padding: 0;
    text-align: center;
`

const SubContainer = styled.div`
    display: flex;
    gap: 3em;
    padding: 2em 5em;
    margin: 0;
    margin-top: -6em;   
`

const Box = styled.a`
    position: relative;
    background-color: #f8f9fa;
    padding: 3em;
    margin: 0;
    border-radius: 8px;
    cursor: pointer;

    &:hover{
        .arrow{
            box-shadow: 0 3px 5px rgba(0,0,0,.04);
            transform: scale(1.1);
        }
    }

    h3{
        font-family: Ruberoid;
        font-size: 2.3em;
        margin: 0;
        margin-top: 1em;
        padding: 0;
    }

    p{
        font-weight: 300;
        font-size: 1.1em;
        margin: 0;
        margin-top: .3em;
        padding: 0;

    }
`

const Arrow = styled.img`
    position: absolute;
    font-size: 1.5em;
    color: #7b2bf9;
    padding: 1em;
    background-color: white;
    border-radius: 50%;
    left: 85%;
    top: 10%;

    &.arrow2{
        left: 92%;
    }
`

const BoxGrow = styled.a`
    position: relative;
    background-color: #f8f9fa;
    display: flex;
    gap: 3em;
    margin: 5em;
    margin-top: -5em;
    align-items: center;
    border-radius: 8px;
    cursor: pointer;

    h3{
        font-family: Ruberoid;
        font-size: 2.3em;
        margin: 0;
        margin-top: 1em;
        padding: 0;
    }

    p{
        font-weight: 300;
        font-size: 1.1em;
        margin: 0;
        margin-top: .3em;
        padding: 0;
    }

    &:hover{
        .arrow2{
            box-shadow: 0 3px 5px rgba(0,0,0,.04);
            transform: scale(1.1);
        }
    }
`

const Image = styled.img`
    width: 35%;
    margin: 0;
    padding: 2em;
    margin-bottom: -2em;
`

function Landing3({id}) {

    useEffect(() => {
        window.scroll(0,0)
      })

  return (
    <div id={id}>
        <Title>Explore Celestia</Title>
        <SubContainer>
            <Box>
                <Arrow src={ArrowImg} alt='Arrow' className='arrow'/>
                <img src={Explore} alt='Explore the ecosystem'/>
                <h3>Explore the ecosystem</h3>
                <p>Explore Celestia’s ecosystem of rollups and modular infrastructure</p>
            </Box>
            <Box>
            <Arrow src={ArrowImg} alt='Arrow' className='arrow'/>
                <img src={Tia} alt='Use Tia'/>
                <h3>Use Tia</h3>
                <p>Pay for blobspace, secure the network, and participate in governance</p>
            </Box>
        </SubContainer>

        <BoxGrow>
            <Arrow src={ArrowImg} alt='Arrow' className='arrow2'/>
            <Image src={Run} alt='Run a light node'/>
            <div>
                <h3>Run a light node</h3>
                <p>Join the first modular data availability network in as little as 2 clicks</p>
            </div>
        </BoxGrow>
    </div>
  )
}

export default Landing3;
