import React from 'react'
import Build from '../../assets/build.webp'
import styled from 'styled-components'

const SubContainer = styled.div`
    display: flex;
    padding: 2em 4em;
    gap: 5em;
`

const Title = styled.h2`
    font-size: 4.6em;
    font-family: Ruberoid;
    letter-spacing: .2px;
    line-height: 1em;
    margin: 0;
    padding: 0;
`

const Description = styled.p`
    margin: 0;
    padding: 0;
    margin-bottom: 1em;
    color: #414141;
    font-size: 1.1em;
    line-height: 1.5;
`

const Button = styled.button`
  border-radius: 8px;
  border: none;
  padding: .9em 1.5em;
  font-size: 1em;
  font-family: 'Ruberoid';
  font-weight: 500;
  cursor: pointer;

  &:hover{
    background-color: #000;
    color: #ffffff;
  }

  &.black{
    background-color: #000;
    color: #ffffff;
    font-weight: bold;

    &:hover{
      background-color: #333;
    }
  }
`

function Landing2() {
  return (
    <section>
        <SubContainer>
            <div>
                <Title>Build whatever</Title>
                <Description>Deploy fast. Launch a blockchain with leading Ethereum rollup frameworks or transform nearly any VM into your own sovereign chain.</Description> 

                <Description> With Celestia underneath, a customizable blockchain becomes as easy to deploy as a smart contract.</Description>
                <div>
                    <Button className='black'>Build Modular</Button>
                    <Button>Deploy</Button>
                </div>

            </div>

            <img src={Build} alt='Build'/>
        </SubContainer>

        {/* <div>
            <h2>Build whatever</h2>
            <p>Deploy fast. Launch a blockchain with leading Ethereum rollup frameworks or transform nearly any VM into your own sovereign chain. <br/>With Celestia underneath, a customizable blockchain becomes as easy to deploy as a smart contract.</p>
            <button>Learn Celestia</button>
        </div> */}
    </section>
  )
}

export default Landing2
