import React, { useEffect, useState } from 'react';
import styled from 'styled-components';
import CelestiaLogo from '../assets/CelestiaLogo.svg';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import '../assets/fonts/stylesheet.css'
import { faAngleUp, faAngleDown } from '@fortawesome/free-solid-svg-icons';
import Navigate from '../navigate';
import Navigation from './sub/Navigation';

const Container = styled.header`
    display: flex; 
    position: sticky;
    top: 0;
    left: 0;
    right: 0;
    z-index: 9999;
    align-items: center;
    justify-content: space-between;
    border: none;
    background-color: ${({scrolled}) => (scrolled ? 'rgba(255, 255, 255, 0.5)' : 'hsla(0,0%,100%,0)')};
    backdrop-filter: ${({scrolled}) => (scrolled ? 'blur(1em)' : 'none')}; 
    padding: ${({scrolled}) => (scrolled ? '1em 4.5em' : '2em 4.3em')};

    img{
        width: ${(props) => (props.scrolled ? '10%' : 'auto')};

        transition: transform 0.4s ease-in-out;
        transform: ${({scrolled}) => (scrolled ? 'scale(1.1, 1.1)' : 'scale(1, 1)')}
    }
    transition: all 0.6s ease-in-out;
`

const Lists = styled.ul`
    list-style: none;
    display: flex;
    gap: 1.3em;
    font-family: 'Ruberoid';
    font-weight: 500;
    font-size: ${({scrolled}) => (scrolled ? '.9em' : '1.15em')};
    transition: all 0.6s ease-in-out;
`

const List = styled.li`
    cursor: pointer;
`

const Arrow = styled(FontAwesomeIcon)`
    padding: 0;
    transition: transform 0.4s ease-in-out;
    transform: ${({hover}) => (hover ? 'rotate(180deg)' : 'rotate(0deg)')}
`

const SubList = styled.div`
    display: ${({show}) => (show ? 'block' : 'none')};
    position: fixed;
    width: 25%;
    top: ${({scrolled}) => (scrolled ? '70%' : '13%')};
    left: 68%;
    font-size: 1.15em;
    font-size: ${({scrolled}) => (scrolled ? '1.5em' : '1.15em')};
    text-wrap: no-wrap;
    margin-left: 1em;
    padding: 1em;
    background: rgba(250, 250, 250, 0.68);
    backdrop-filter: blur(.5em);
    border-radius: 10px;
`

const SubContainer = styled.div`
    border: none;
    width: 90%;
    display: flex;
    gap: 1.5em;
    align-items: center;
    margin: 0 .5em;
    padding: .5em 0;



    &:not(:last-child){
        border-bottom: .1px solid #d9d5df;
    }

    img{
        background: #000;
        padding: .1em;
        border-radius: 50%;
        width: 7%;
        height: 50%;
    }

    &:hover{
        img{
            background-color: #7b2bf9;
        }

        .subtitle{
            color: #7b2bf9;
        }
    }
`

const SubTitles = styled.div`
    p{
        margin: 0;
        padding: 0;
        padding-bottom: .2em;
    }



    display: flex;
    flex-direction: column;
`

const Title = styled.p`
    color: #777;
    font-size: .6em;
`

const SubTitle = styled.p`
    color: #000;
    font-size: .8em;
    font-weight: bold;
`

function Heading() {

    const [hover, setHover] = useState(null);
    const [scrolled, setScrolled] = useState(false);

    function handleHover(index){
        setHover(index)
    }

    function handleNoHover(){
        setHover(null)
    }

    useEffect(() => {
        function handleScroll(){
            if(window.scrollY > 160){

                setScrolled(true)
            }else{
                setScrolled(false)
            }
        }

        window.addEventListener('scroll', handleScroll)

        return() => {
            window.removeEventListener('scroll', handleScroll)
        }
    }, [])

  return (
    <Container scrolled={scrolled}>
        <img src={CelestiaLogo} alt='Celestia Logo'/>
        <Navigation   scrolled={scrolled} nav={nav} index={index} handleHover={handleHover} handleNoHover={handleNoHover} hover={hover} faAngleUp={faAngleUp} faAngleDown={faAngleDown} sub={sub}  />
    </Container>
  )
}

export default Heading
