import { faCalendar, faCalendarAlt, faCircle, faCircleDot, faDotCircle, faLocation, faLocationDot } from '@fortawesome/free-solid-svg-icons'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import React from 'react'
import styled from 'styled-components'
import Hackathon from '../assets/hackathonImage.webp'

const Container = styled.section`
    margin-top: 5em;
    padding-top: 1em;
    padding-left: 5em;

    h1{
        font-family: "Ruberoid";
        font-size: 7em;
        margin-bottom: 0;
        padding-bottom: 0;
    }
`

const SubTitle = styled.p`
    font-family: "Ruberoid";
    margin: 0;
    padding: 0;
    font-weight: 500;
    font-size: 1.2em;
`

const HackContainer = styled.div`
    display: flex;
    gap: 1em;
    width: 90%;
    align-items: center;
    background-color: #fff;
    border: 1px solid #e3e3e3;
    border-radius: 8px;
    box-shadow: 0 2px 2px rgba(0,0,0,.03);
    display: flex;
    flex-wrap: wrap;
    justify-content: space-between;
    padding: .5em;
    margin-top: 2em; 

    img{
        width: 50%;
        border-radius: 8px;
    }
`

const Sub = styled.div`
    margin: 0;
    margin-right: 4em;
    margin-bottom: 6em;
    padding: 0;
    width: 40%;

    h3{
        font-family: "Ruberoid";
        font-size: 3em;
        margin-bottom: 0;
        padding-bottom: 0;
    }
`

const FlexButton = styled.div`
    display: flex;
    width: 100%;
    gap: 3em;
    align-items: space-between;

    p{
        font-weight: bold;
    }
`

const Icon = styled(FontAwesomeIcon)`
    font-size: .3em;
    align-self: center;
`

const Line = styled.div`
    border-bottom: 1px solid #ececec;
    padding-bottom: 2em;
    margin-bottom: 1em;
`

const Button2 = styled.button`
  border-radius: 8px;
  border: none;
  font-size: 1em;
  font-family: "Ruberoid";
  font-weight: 500;
  cursor: pointer;
  width: max-content;
  text-align: center;

  &:hover {
    background-color: #000;
    color: #ffffff;
  }

  &.black {
    background-color: #000;
    color: #ffffff;
    font-weight: bold;

    &:hover {
      background-color: #333;
    }
  }

  @media (min-width: 768px) {
    width: auto;
    padding: 0 1.5em;
  }
`

function Events() {
  return (
    <Container>
        <h1>Events</h1>
        <SubTitle>Hangout with the Celestia community IRL or online.</SubTitle>

        <HackContainer>
            <img src={Hackathon} alt='Hackathon'/>
            <Sub>
                <Line>
                    <h3>Celestia hackathon</h3>
                    <FlexButton>
                        <p><FontAwesomeIcon icon={faCalendar}/> Coming soon!!</p>
                        <Icon icon={faCircle}/>
                        <p><FontAwesomeIcon icon={faLocationDot}/> Online</p>
                    </FlexButton>
                </Line>

                <Button>View event</Button>
            </Sub>
        </HackContainer>

        <div>
            <div>
                <h3>Community events</h3>
                <button>Submit your Event</button>
            </div>

            <div>View Past Events</div>
        </div>
    </Container>
  )
}

export default Events
