import React from 'react'
import styled from 'styled-components'

const Container = styled.div`
    h2, h3{
        font-family: Ruberoid;
        font-weight: 500; 
    }

    h2{
        font-size: 1.8em;  
    }

    h3{
        font-size: 1.5em;
    }

    li{
        border-top: 1.5px solid black;
        padding: 3em 0;
        font-size: .9em;
    }
`

function Privacy3() {
  return (
    <Container>
        <div>
            <h2>1. UPDATING THIS PRIVACY POLICY</h2>
            <p>We may modify this Privacy Policy from time to time in which case we will update the “Last Revised” date at the top of this Privacy Policy. If we make material changes to the way in which we use information we collect, we will use reasonable efforts to notify you (such as by emailing you at the last email address you provided us, by posting notice of such changes on the Services, or by other means consistent with applicable law) and will take additional steps as required by applicable law. If you do not agree to any updates to this Privacy Policy, please do not access or continue to use the Services.</p>
            <p>You are advised to review this Privacy Policy periodically for any changes. Changes to this Privacy Policy are effective when they are posted on this page.</p>
        </div>

        <div>
            <h2>2. YOUR LEGAL RIGHTS</h2>
            <ol>
                <li>Right of access and information

                You have the right to request from us free confirmation of the processing of the data or information in question and to be informed of such data and to receive further information and a copy of the data in accordance with the provisions of the law. The subject of the information is the stored personal data themselves, the origin of the data, their recipients, and the purpose of the data processing (cf. Art. 15 GDPR).</li>
            </ol>
        </div>
    </Container>
  )
}

export default Privacy3
