import React, { useState } from 'react'
import Framework from '../../Framework'
import styled from 'styled-components'
import BuildCard from './BuildCard'

const Container = styled.section`
    width: 90%;
    display: flex;
    flex-direction: column;
    align-items: center;
    margin: 0 auto;
    margin-top: 7em;
    margin-bottom: 30em;
`

const Title = styled.h2`
    font-size: 4.8em;
    margin: 0;
    padding: 0;
    font-family: Ruberoid;
`

const SubTitle = styled.p`
    margin: 0;
    padding: 0;
    font-weight: 300;
`

const ButtonContainer = styled.div`
    margin: 2em;
    display: flex;
    gap: 1em;

    button{
        padding: 1em 2em;
        padding-top: 1.1em;
        border-radius: 5px;
        border: none;
        background: inherit;
        font-family: Ruberoid;
        font-size: .9em;
        font-weight: bold;

        &.active{
            background: #7b2bf9;
            color: #fff;

        }
    }
`

const FrameWorkCards = styled.div`
    display: flex;
    flex-wrap: wrap;
    gap: 2.5em;
`

function Build2() {
    const [selectedFramework, setSelectedFramework] = useState('ALL')

    function handleSelectFramework(button){
        setSelectedFramework(button)
    }

  return (
    <Container>
        <Title>Choose a framework</Title>
        <SubTitle>Get started quickly by using Celestia with leading rollup frameworks.</SubTitle>
        <ButtonContainer>
            {
                ['ALL', 'ETHEREUM', 'SOVEREIGN'].map((button) => {
                    return(<button onClick={() => handleSelectFramework(button)} className={selectedFramework===button ? 'active' : null}>{button}</button>)
                })
            }
        </ButtonContainer>

        <FrameWorkCards>
            {
                Framework.map((framework) => {

                    if(selectedFramework === 'ALL'){

                        return(
                            <BuildCard framework={framework}/>
                        )
                    }

                    if(selectedFramework === 'ETHEREUM' || 'SOVEREIGN'){
                        return(
                            <BuildCard framework={framework}/>
                        )
                    } 


            })}
        </FrameWorkCards>
    </Container>
  )
}

export default Build2
