import React from 'react'
import styled from 'styled-components'
import LazyBridging from '../../assets/Celestia_Lazybridging.webp'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faArrowRight } from '@fortawesome/free-solid-svg-icons'
import CelestiaBlogs from '../../celestiaBlogs.js'

const Container = styled.div`
    padding: 0 4em;
    padding-bottom: 3em;

    h2{
        font-size: 4.4em;
        font-family: Ruberoid;
        letter-spacing: .2px;
        line-height: 1em;
        margin: 0;
        padding: 0;
    }
`

const ArrowSpan = styled(FontAwesomeIcon)`
    color: #7b2bf9;
    transform: rotate(-45deg);
`

const Read = styled.div`

    width: 80%;
    text-align: right;

    p{
        font-family: Ruberoid;
        font-weight: 600;
        font-size: 1em;
    }


    &:hover{
        p{
            color: #7b2bf9;
        }

        .hoverIcon{
            transform: rotate(0deg);
        }
    }
`

const FlexContainer = styled.div`
    display: flex;
    gap: 1em;   
    margin-top: 3em;
    font-family: Ruberoid;

    img{
        width: 100%;
        border-radius: 5px;
    }

    a{
        text-decoration: none;
        color: inherit;
    }
`

function Landing5() {
  return (
    <Container>
        <h2>Blog</h2>
        <FlexContainer>
            <a href='https://blog.celestia.org/lazybridging/'>
                <img src={LazyBridging} alt='Lazy Bridging Blog Heading'/>
                <p>BLOG</p>
                <h3>
                    Lazybridging: the Celestia endgame to solve 
                    fragmentation and stream assets from anywhere
                </h3>
                <Read><p>Read now <ArrowSpan icon={faArrowRight} className='hoverIcon'/></p></Read>
            </a>
            <div>
                {
                    CelestiaBlogs.map((blog) => {
                        return(
                            <a href={blog.link}>{blog.title}</a>
                        )
                    })
                }
            </div>
        </FlexContainer>
    </Container>
  )
}

export default Landing5
