import React, { lazy } from 'react'
import styled from 'styled-components'
import LazyBridging from '../../assets/Celestia_Lazybridging.webp'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faArrowRight } from '@fortawesome/free-solid-svg-icons'

const Container = styled.div`
    padding: 0 4em;
    padding-bottom: 3em;

    h2{
        font-size: 4.4em;
        font-family: Ruberoid;
        letter-spacing: .2px;
        line-height: 1em;
        margin: 0;
        padding: 0;
    }
`

const Read = styled.div`

    width: 80%;
    text-align: right;

    p{
        font-family: Ruberoid;
        font-weight: 600;
        font-size: 1em;
    }


    &:hover{
        p{
            color: #7b2bf9;
        }

        .hoverIcon{
            transform: rotate(0deg);
        }
    }
`

function Landing5() {
  return (
    <Container>
        <h2>Blog</h2>
        <div>
            <div>
                <img src={LazyBridging}/>
                <p>BLOG</p>
                <h3>
                    Lazybridging: the Celestia endgame to solve 
                    fragmentation and stream assets from anywhere
                </h3>
                <Read><p>Read now <ArrowSpan icon={faArrowRight} className='hoverIcon'/></p></Read>
            </div>
            <div></div>
        </div>
    </Container>
  )
}

export default Landing5
