import React, { useEffect, useState } from 'react';
import styled from 'styled-components';
import CelestiaLogo from '../assets/CelestiaLogo.svg';
import '../assets/fonts/stylesheet.css'
import Navigation from './Navigation/Navigation';
import { Link } from 'react-router-dom';


const Container = styled.header`
    display: flex; 
    position: sticky;
    top: 0;
    left: 0;
    right: 0;
    z-index: 9999;
    align-items: center;
    justify-content: space-between;
    border: none;
    background-color: ${({scrolled}) => (scrolled ? 'rgba(255, 255, 255, 0.5)' : 'hsla(0,0%,100%,0)')};
    backdrop-filter: ${({scrolled}) => (scrolled ? 'blur(1em)' : 'none')}; 
    padding: ${({scrolled}) => (scrolled ? '1em 4.5em' : '2em 4.3em')};
    transition: all 0.6s ease-in-out;

    img{
        width: ${(props) => (props.scrolled ? '60%' : 'auto')};
        cursor: pointer;
        transition: transform 0.4s ease-in-out;
        transform: ${({scrolled}) => (scrolled ? 'scale(1.1, 1.1)' : 'scale(1, 1)')}
    }


`

const Hamburger = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: space-around;
  background: #000;
  border: none;
  border-radius: 5px;
  height: 25px;
  width: 100px !important;
  padding: 8px 7px;
  width: 15px;
  z-index: 101;
  cursor: pointer;

  div {
    width: 100%;
    height: 3px;
    background-color: #fff;
  }

  @media (min-width: 992px) {
    display: none;
  }
`;

function Heading() {
    const [scrolled, setScrolled] = useState(false);

    useEffect(() => {
        function handleScroll(){
            if(window.scrollY > 160){

                setScrolled(true)
            }else{
                setScrolled(false)
            }
        }

        window.addEventListener('scroll', handleScroll)

        return() => {
            window.removeEventListener('scroll', handleScroll)
        }
    }, [])

  return (
    <Container scrolled={scrolled}>
        <Hamburger>
            <div/>
            <div/>
            <div/>
        </Hamburger>
        <Link to='/'>
            <img src={CelestiaLogo} alt='Celestia Logo' />
        </Link>
        <Navigation scrolled={scrolled}/>
    </Container>
  )
}

export default Heading
