import React, { useState } from 'react';
import styled from 'styled-components';
import CelestiaLogo from '../assets/CelestiaLogo.svg';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import '../assets/fonts/stylesheet.css'
import { faAngleUp, faAngleDown } from '@fortawesome/free-solid-svg-icons';
import Navigate from '../navigate';

const Container = styled.header`
    display: flex; 
    padding: 2em 4.3em;
    align-items: center;
    justify-content: space-between;
    position: relative;
`

const Lists = styled.ul`
    list-style: none;
    display: flex;

    gap: 1.3em;
    font-family: 'Ruberoid';
    font-weight: 500;
    font-size: 1.15em;
`

const List = styled.li`
    cursor: pointer;
`

const Arrow = styled(FontAwesomeIcon)`
    padding: 0;
    transition: transform 0.4s ease-in-out;
    transform: ${({hover}) => (hover ? 'rotate(180deg)' : 'rotate(0deg)')}
`

const SubList = styled.div`
    display: ${({show}) => (show ? 'block' : 'none')};
    position: absolute;
    background-color: red;
    top: 70%;
    left: 70%;
    width: max-content;
    padding: 1em;
    border-radius: 10px;
`

function Heading() {

    const [hover, setHover] = useState(null)

    function handleHover(index){
        setHover(index)
    }

    function handleNoHover(){
        setHover(null)
    }

  return (
    <Container>
        <img src={CelestiaLogo} alt='Celestia Logo'/>
        <nav>
            <Lists>
                {
                    Navigate.map((nav, index) => {
                        return(
                            <List 
                                key={index} 
                                onMouseEnter={() => handleHover(index)} 
                                onMouseLeave={handleNoHover}
                            >
                                {nav.nav} <Arrow hover={hover === index} icon={hover === index ? faAngleUp : faAngleDown}/>
                                <SubList show={hover === index}>
                                    {nav.subNav.map((sub) => {
                                        return (
                                            <div>
                                                <h3>{sub.subTitle}</h3>
                                                <p>{sub.title}</p>
                                            </div>
                                        )
                                    })}
                                </SubList>
                            </List>
                        ) 
                    })
                }
            </Lists>
        </nav>
    </Container>
  )
}

export default Heading
