import React, { useState } from 'react'
import Framework from '../../Framework'
import styled from 'styled-components'
import BuildCard from './BuildCard'

const Container = styled.section`
    width: 90%;
    display: flex;
    flex-direction: column;
    align-items: center;
    margin: 0 auto;
    margin-top: 7em;
    margin-bottom: 30em;
`

const Title = styled.h2`
    font-size: 4.8em;
    margin: 0;
    padding: 0;
    font-family: Ruberoid;
`

const SubTitle = styled.p`
    margin: 0;
    padding: 0;
    font-weight: 300;
`

const ButtonContainer = styled.div`
    margin: 2em;
    display: flex;


    button{
        padding: .5em 1em;
        padding-top: .7em;
        border-radius: 5px;
        border: none;
        background: inherit;
        font-family: Ruberoid;
        font-size: .9em;
        font-weight: bold;
        transition: all 0.5s ease-in-out;

        &.active{
            background: #7b2bf9;
            color: #fff;

        }
    }
`

const FrameWorkCards = styled.div`
    display: flex;
    flex-wrap: wrap;
    gap: 2em;
    justify-content: left;
`

function Build2() {
    const [selectedFramework, setSelectedFramework] = useState('ALL')

    function handleSelectFramework(button){
        setSelectedFramework(button)
    }

  return (
    <Container>
        <Title>Rollups-as-a-Service</Title>
        <SubTitle>Deploy end-to-end on managed infrastructure using a Rollup-as-a-Service provider.</SubTitle>
        <ButtonContainer>
            {
                ['ALL', 'ARBITRUM ORBIT', 'OP STACK', 'POLYGON CDK', 'STARKNET STACK'].map((button) => {
                    return(
                        <button 
                            onClick={() => handleSelectFramework(button)} 
                            className={selectedFramework===button ? 'active' : null} 
                            style={{cursor:pointer}}
                        >
                            {button}
                        </button>
                    )
                })
            }
        </ButtonContainer>

        <FrameWorkCards>
            {
                Framework.map((framework) => {
                    if (selectedFramework === 'ALL' || framework.stack === selectedFramework) {
                        return <BuildCard key={framework.title} framework={framework} />;
                    }
                })
            }
        </FrameWorkCards>
    </Container>
  )
}

export default Build2
