import React from "react";
import styled from "styled-components";



function Navigation({scrolled, nav, index, handleHover, handleNoHover, hover, faAngleUp, faAngleDown}) {
    return (<nav>
        <Lists scrolled={scrolled}>
            {Navigate.map((nav, index) => {
    return <List key={index} onMouseEnter={() => handleHover(index)} onMouseLeave={handleNoHover}>
                            {nav.nav} <Arrow hover={hover === index} icon={hover === index ? faAngleUp : faAngleDown} />
                            <SubList show={hover === index} scrolled={scrolled} onMouseEnter={() => handleHover(index)} onMouseLeave={handleNoHover}>
                                    {nav.subNav.map((sub, index) => {
        return <SubContainer key={index}>
                                                <img src={sub.icon} alt={sub.alt} />
                                                <SubTitles>
                                                    <Title>{sub.title}</Title>
                                                    <SubTitle className='subtitle'>{sub.subTitle}</SubTitle>
                                                </SubTitles>
                                            </SubContainer>;
        })}
                            </SubList>
                        </List>;
})}
        </Lists>
    </nav>);
}

export default Navigation
