import React from 'react'
import styled from 'styled-components'
import SocialBG from '../assets/CommunityBG.webp'
import Ecosystem from '../assets/ecosystem.webp'
import Social1 from './Social/Social1'
import Social2 from './Social/Social2'
import Social3 from './Social/Social3'

const Container = styled.section`
    padding: 6em;
    padding-right: 9em;
    display: flex;
    flex-direction: column;
    gap: 8em;

    &:before{
        background: url(${SocialBG}) no-repeat;
        background-size: 40%;
        background-position: right;
        content: "";
        height: 100%;
        left: -5%;
        position: absolute;
        top: 40%;
        right: 20%;
        width: 100%;
        z-index: -1;
    }
`

const Ctn = styled.div`
    display: flex;
    gap: 8em;

    img{
        width: 50%;
    }
`

function Social() {
  return (
    <Container>
        <Social1/>
        <Social2/>
        <Social3/> 
        <Ctn>
            <img src={Ecosystem} alt='Ecosystem'/>
            <div>
                <h2>Ecosystem</h2>
                <p>Discover a wide range of apps and services built in the Celestia ecosystem.</p>
                <button>Dive in</button>
            </div>
        </Ctn>   
    </Container>
  )
}

export default Social
