import React, { useState } from 'react'
import FooterImg from '../assets/footer.webp'
import styled from 'styled-components'
import ReCAPTCHA from 'react-google-recaptcha';

const Container = styled.footer`
    margin-top: -25em;
    padding: 0 4em;
    padding-bottom: 3em;
    padding-top: 25em;
    position: relative;
    max-width: 100%;
    display: flex;  

    &:before{
        background: url(${FooterImg}) no-repeat scroll bottom transparent;
        background-size: 100% 1200px;
        bottom: 0;
        content: "";
        height: 100%;
        left: 0;
        position: absolute;
        width: 100%;
        z-index: -2;    
    }
`;

const Title = styled.h2`
    color: #fff;
    font-family: Ruberoid;
    font-weight: 700;
    letter-spacing: 0;
    margin-bottom: 20px;
    font-size: 60px;
    line-height: 48px;
`

const Newsletter = styled.div`
    display: flex;
    flex-direction: column;
    gap: 1.5em;

    input{
        padding: 1em; 
        border-radius: 5px;
        width: 60%;
        border: none;
        font-family: inherit;
        font-size: 1em;
    }

    button{
        background: #000;
        border: none;
        border-radius: 6px;
        color: #fff;
        font-family: Ruberoid;
        font-size: 1em;
        font-weight: 700;
        letter-spacing: .2px;
        line-height: 1.25em;
        padding: 1em 2em;
        text-align: center;
        text-decoration: none;
        width: max-content;
        cursor: ${({ disabled }) => (disabled ? 'not-allowed' : 'pointer')};
    }
`

const LowerTitle = styled.h3`
    color: #fff;
    font-family: Ruberoid;
    font-weight: 700;
    letter-spacing: 0;
    margin-bottom: 20px;
    font-size: 1.5em;
    line-height: 48px;
`

const SubContainer = styled.p`
    display: flex;
    flex-direction: column;
    gap: 1em;
    color: #fff;
    font-size: 1em;
    line-height: 12px;
    flex-wrap: no-wrap;
    white-space: no-wrap;
`

const FlexContainer = styled.div`
    display: flex;
    gap: 2em;
    // margin-left: -15em;
`

function Footer() {
    const [capVal, setCapVal] = useState(null)
  return (
    <Container>
        <Newsletter>
            <Title>Subscribe to our Newsletter</Title>
            <input type='email' placeholder='mail@celestia.com'/>
            <ReCAPTCHA sitekey='6LeB8ZYqAAAAANAEy8OS9EjED1H--PSxkoKAddPk' onChange={val => setCapVal(val)}/>
            <button disabled={!capVal}>Subscribe</button>
        </Newsletter>
        <FlexContainer>
            <div>
                <LowerTitle>Developers</LowerTitle>
                {['Build', 'Docs', 'Github', 'FAQ'].map((list) => {
                    return(<SubContainer>{list}</SubContainer>)
                })}
            </div>
            <div>
                <LowerTitle>Resources</LowerTitle>
                {['Technology', 'Glossary', 'Blog', 'Podcast', 'Resources'].map((list) => {
                    return(<SubContainer>{list}</SubContainer>)
                })}
            </div>
            <div>
                <LowerTitle>Misc</LowerTitle>
                {['Brand', 'Press', 'Careers', 'Terms of Service', 'Privacy Policy'].map((list) => {
                    return(<SubContainer>{list}</SubContainer>)
                })}
            </div>
            <div>
                <LowerTitle>Follow us</LowerTitle>
            </div>
        </FlexContainer>


    </Container>
  );
}

export default Footer;
