import React from 'react';
import styled from 'styled-components';
import HomepageBG from '../../assets/HomepageBG.webp';
import { Link } from 'react-router-dom';

const Container = styled.div`
  width: 100%;

  &:before{
    background-image: url(${HomepageBG});
    background-size: cover;
    content: "";
    height: 100%;
    left: 0;
    position: absolute;
    top: 0;
    width: 100%;
    z-index: -1;
    padding-bottom: 10em;
  }
`

const Title = styled.h1`
  font-family: 'Ruberoid';
  font-weight: bold;
  font-size: 6em;
  padding: 1.2em;
  text-align: center;
  letter-spacing: -.05em;
  line-height: 1;
  margin-bottom: 0;
  padding-bottom: 0;
`

const SubTitle = styled.p`
  font-family: 'Ruberoid';
  font-size: 1.3em;
  text-align: center;
  padding: 1em 16em;
  font-weight: 500;
  margin: 0;
`

const Buttons = styled.div`
  display: flex;
  gap: 1.5em;
  align-items: center;
  justify-content: center;
  padding: .9em;
`

const Button = styled.button`
  border-radius: 8px;
  border: none;
  padding: .9em 1.5em;
  font-size: 1em;
  font-family: 'Ruberoid';
  font-weight: 500;
  cursor: pointer;

  &:hover{
    background-color: #000;
    color: #ffffff;
  }

  &.black{
    background-color: #000;
    color: #ffffff;
    font-weight: bold;

    &:hover{
      background-color: #333;
    }
  }
`

function Landing1() {
  return (
    <Container>
        <Title>The first modular blockchain network</Title>
        <SubTitle>Celestia is a modular data availability network that makes it easy for anyone to securely launch their own blockchain.</SubTitle>
        <Buttons>
            <Link to='/build'><Button className='black'>Build modular</Button></Link>
            <Button>Explore</Button>
        </Buttons>
    </Container>
  )
}

export default Landing1
