import React from 'react'
import styled from 'styled-components'
import Events1 from './Events/Events1'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faArrowRight } from '@fortawesome/free-solid-svg-icons'

const Container = styled.section`
    margin-top: 5em;
    padding-top: 1em;
    padding-left: 5em;
    z-index: 2;
    position: relative;
    display: flex;
    flex-direction: column;
    gap: 3em;
`

const FlexContainer = styled.div`
    display: flex;
    padding-right: 6.5em;
    justify-content: space-between;

    h3{
        font-family: "Ruberoid";
        font-size: 3em;
        font-weight: 700;
        margin: 0;
    }

    a{
        background: #fff;
        border: 1px solid #e3e3e3;
        border-radius: 8px;
        box-shadow: 0 2px 2px rgba(0,0,0,.03);
        color: #000;
        font-family: "Ruberoid";
        font-size: 1em;
        font-weight: 700;
        height: fit-content;
        letter-spacing: .2px;
        line-height: 1;
        padding: .8em 1em;
        transition: background-color .3s ease;
        cursor: pointer;

        &:hover{
            background: #f1f1f1;
            .arrow{
                color: #7b2bf9;
                transform: rotate(0deg);
            }
        }

        .arrow{
            transform: rotate(-45deg);
            color: #7b2bf9;
        }

    }
`

const Past = styled.a`
    background: #fff;
    border: 1px solid #e3e3e3;
    border-radius: 8px;
    box-shadow: 0 2px 2px rgba(0,0,0,.03);
    color: #000;
    font-family: "Ruberoid";
    font-size: 14px;
    font-weight: 700;
    height: -webkit-fit-content;
    height: fit-content;
    letter-spacing: .2px;
    line-height: 19px;
    padding: 10px 16px;
    transition: background-color .3s ease;
`

function Events() {
  return (
    <Container>
        <Events1/>

        <>
            <FlexContainer>
                <h3>Community events</h3>
                <a>Submit your Event  <FontAwesomeIcon icon={faArrowRight} className="arrow" /></a>
            </FlexContainer>

            <Past>View Past Events</Past>
        </>
    </Container>
  )
}

export default Events
