import React from 'react'
import styled from 'styled-components'
import SocialBG from '../assets/CommunityBG.webp'
import Ecosystem from '../assets/ecosystem.webp'
import Social1 from './Social/Social1'
import SocialList from '../SocialList'

const Container = styled.section`
    padding: 6em;
    padding-right: 9em;
    display: flex;
    flex-direction: column;
    gap: 4em;

    &:before{
        background: url(${SocialBG}) no-repeat;
        background-size: 40%;
        background-position: right;
        content: "";
        height: 100%;
        left: -5%;
        position: absolute;
        top: 40%;
        right: 20%;
        width: 100%;
        z-index: -1;
    }
`

const Ctn = styled.div`
    display: flex;
    width: 100%;
`

const SubCtn = styled.div`
    min-width: 43%;

    h2{
        font-size: 4.5em;
        font-family: "Ruberoid";
        line-height: 1;
        margin: 0;
        width: 30%;
        padding: 0;
    }

    p{
        width: 70%;
        font-weight: 300;
        font-size: 1em;
    }
`

const SocialCtn = styled.div`
    display: flex;
    flex-wrap: wrap;
    gap: 3em;
`

const SocialContainer = styled.div`
    width: 46%;
    display: flex;
    gap: .8em;

    img{
        width: 20%;
        height: 55%;
    }

    p, h3{
        margin: 0;
        padding: 0;
        width: 100%;
    }

    h3{
        font-size: 1.8em;
        font-family: "Ruberoid";
    }

    p{
        font-weight: 300;
        font-size: .9em;
    }
`

function Social() {
  return (
    <Container>
        <Social1/>
        <Ctn>
            <SubCtn>
                <h2>Social channels</h2>
                <p>Communication channels for the community, discussions, updates, and news.</p>
            </SubCtn>

            <SocialCtn>
                {SocialList.map((social) => {
                    return(
                        <SocialContainer key={social.social}>
                            <img src={social.icon} alt={social.social}/>
                            <div>
                                <h3>{social.social}</h3>
                                <p>{social.sub}</p>
                            </div>
                        </SocialContainer>
                    )
                })}
            </SocialCtn>
        </Ctn>   
        <div>
            <div>

            </div>
            <div>
                <h2>Explore</h2>
                <p>Keep up with new resources and content.</p>
            </div>
        </div>  
        <div>
            <img src={Ecosystem} alt='Ecosystem'/>
            <div>
                <h2>Ecosystem</h2>
                <p>Discover a wide range of apps and services built in the Celestia ecosystem.</p>
                <button>Dive in</button>
            </div>
        </div>   
    </Container>
  )
}

export default Social
