import React, { useEffect, useState } from 'react';
import styled from 'styled-components';
import CelestiaLogo from '../assets/CelestiaLogo.svg';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import '../assets/fonts/stylesheet.css'
import { faAngleUp, faAngleDown } from '@fortawesome/free-solid-svg-icons';
import Navigate from '../navigate';

// const Container = styled.header`
//     display: flex; 
//     position: sticky;
//     top: 0;
//     left: 0;
//     right: 0;
//     padding: 2em 4.3em;
//     z-index: 9999;
//     align-items: center;
//     justify-content: space-between;
//     border: ${(props) => (props.scrolled ? '2px solid rgba(255, 255, 255, 0.5)' : 'none')}; 
//     background-color: ${(props) => (props.scrolled ? 'rgba(255, 255, 255, 0.2)' : 'hsla(0,0%,100%,0)')};
//     backdrop-filter: ${(props) => (props.scrolled ? 'blur(1em)' : 'none')}; 
// `

const Lists = styled.ul`
    list-style: none;
    display: flex;
    gap: 1.3em;
    font-family: 'Ruberoid';
    font-weight: 500;
    font-size: 1.15em;
`

const List = styled.li`
    cursor: pointer;
`

const Arrow = styled(FontAwesomeIcon)`
    padding: 0;
    transition: transform 0.4s ease-in-out;
    transform: ${({hover}) => (hover ? 'rotate(180deg)' : 'rotate(0deg)')}
`

const SubList = styled.div`
    display: ${({show}) => (show ? 'block' : 'none')};
    position: fixed;
    width: 25%;
    top: 15%;
    left: 68%;
    text-wrap: no-wrap;
    margin-left: 1em;
    padding: 1em;
    background: rgba(250, 250, 250, 0.68);
    backdrop-filter: blur(.5em);
    border-radius: 10px;
`

const SubContainer = styled.div`
    border: none;
    width: 90%;
    display: flex;
    gap: 1.5em;
    align-items: center;
    margin: 0 .5em;
    padding: .5em 0;



    &:not(:last-child){
        border-bottom: .1px solid #d9d5df;
    }

    img{
        background: #000;
        padding: .1em;
        border-radius: 50%;
        width: 7%;
        height: 50%;
    }
`

const SubTitles = styled.div`
    p{
        margin: 0;
        padding: 0;
        padding-bottom: .2em;
    }

    display: flex;
    flex-direction: column;
`

const Title = styled.p`
    color: #777;
    font-size: .6em;
`

const SubTitle = styled.p`
    color: #000;
    font-size: .8em;
    font-weight: bold;
`

function Heading() {

    const [hover, setHover] = useState(null);
    const [scrolled, setScrolled] = useState(false);

    function handleHover(index){
        setHover(index)
    }

    function handleNoHover(){
        setHover(null)
    }

    useEffect(() => {
        function handleScroll(){
            if(window.scrollY > 10){
                setScrolled(true)
            }else{
                setScrolled(false)
            }
        }

        window.addEventListener('scroll', handleScroll)

        return() => {
            window.removeEventListener('scroll', handleScroll)
        }
    }, [])

  return (
    <Container scrolled={scrolled}>
        <img src={CelestiaLogo} alt='Celestia Logo'/>
        <nav>
            <Lists>
                {
                    Navigate.map((nav, index) => {
                        return(
                            <List 
                                key={index} 
                                onMouseEnter={() => handleHover(index)} 
                                onMouseLeave={handleNoHover}
                            >
                                {nav.nav} <Arrow hover={hover === index} icon={hover === index ? faAngleUp : faAngleDown}/>
                                <SubList show={hover === index}>
                                    {nav.subNav.map((sub) => {
                                        return (
                                            <SubContainer>
                                                <img src={sub.icon} alt={sub.alt}/>
                                                <SubTitles>
                                                    <Title>{sub.title}</Title>
                                                    <SubTitle>{sub.subTitle}</SubTitle>
                                                </SubTitles>
                                            </SubContainer>
                                        )
                                    })}
                                </SubList>
                            </List>
                        ) 
                    })
                }
            </Lists>
        </nav>
    </Container>
  )
}

export default Heading
