import React from 'react'
import styled from 'styled-components'
import Explore from '../../assets/explore.svg'
import Tia from '../../assets/tia.svg'
import Run from '../../assets/run.webp'
import { faArrowUp } from '@fortawesome/free-solid-svg-icons'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'

const Title = styled.h2`
    font-size: 4.4em;
    font-family: Ruberoid;
    letter-spacing: .2px;
    line-height: 1em;
    margin: 0;
    padding: 0;
    text-align: center;
`

const SubContainer = styled.div`
    display: flex;
    gap: 3em;
    padding: 2em 5em;
    margin: 0;
    margin-top: -6em;   
`

const Box = styled.a`
    position: relative;
    background-color: #f8f9fa;
    padding: 3em;
    margin: 0;
    border-radius: 8px;
    cursor: pointer;

    &:hover{
        .arrow{
            box-shadow: 0 3px 5px rgba(0,0,0,.04);
            transform: scale(1.5, 1.5);
            transform: rotate(45deg);
        }
    }

    h3{
        font-family: Ruberoid;
        font-size: 2.3em;
        margin: 0;
        margin-top: 1em;
        padding: 0;
    }

    p{
        font-weight: 300;
        font-size: 1.1em;
        margin: 0;
        margin-top: .3em;
        padding: 0;

    }
`

const Arrow = styled(FontAwesomeIcon)`
    transform: rotate(45deg);
    position: absolute;
    font-size: 1.5em;
    color: #7b2bf9;
    padding: .8em 1em;
    background-color: white;
    border-radius: 50%;
    top: 3;
    left: 85%;
`

const BoxGrow = styled.div`
    position: relative;
    background-color: #f8f9fa;
    display: flex;
    margin: 5em;
    border-radius: 8px;
    cursor: pointer;

    h3{
        font-family: Ruberoid;
        font-size: 2.3em;
        margin: 0;
        margin-top: 1em;
        padding: 0;
    }

    p{
        font-weight: 300;
        font-size: 1.1em;
        margin: 0;
        margin-top: .3em;
        padding: 0;

    }
`

const Image = styled.img`
    width: 35%;
    margin: 0;
    // padding: 1.5em 0;
    padding: 2em;
    padding-bottom: 2em;
    margin-bottom: 3em;
`

function Landing3() {
  return (
    <>
        <Title>Explore Celestia</Title>
        <SubContainer>
            <Box>
                <Arrow icon={faArrowUp} className='arrow'/>
                <img src={Explore} alt='Explore the ecosystem'/>
                <h3>Explore the ecosystem</h3>
                <p>Explore Celestiaâ€™s ecosystem of rollups and modular infrastructure</p>
            </Box>
            <Box>
                <Arrow icon={faArrowUp} className='arrow'/>
                <img src={Tia} alt='Use Tia'/>
                <h3>Use Tia</h3>
                <p>Pay for blobspace, secure the network, and participate in governance</p>
            </Box>
        </SubContainer>
        <BoxGrow>
            <Arrow icon={faArrowUp}/>
            <Image src={Run} alt='Run a light node'/>
            <div>
                <h3>Run a light node</h3>
                <p>Join the first modular data availability network in as little as 2 clicks</p>
            </div>
        </BoxGrow>
    </>
  )
}

export default Landing3;
