import { faArrowUp } from '@fortawesome/free-solid-svg-icons'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import React from 'react'
import styled from 'styled-components'

const Container = styled.div`
    display: flex;
    gap: 2em;
    justify-content: space-between;
    padding: 5em;

    h3{
        font-size: 1.8em;
        font-weight: 400;
        font-family: Ruberoid;
        padding: 0;
        margin: 0;
    }
`

const Sub = styled(FontAwesomeIcon)`
  padding-right: 1em;
  padding-left: 1em;
  transform: rotate(45deg);
`

function Careers3() {
  return (
    <Container>
        <div>
            <h3>Perks</h3>
            <button>CURRENT OPENINGS <Sub icon={faArrowUp} className="arrow" /></button>
        </div>

        <ul>
            <li>Employment benefits</li>
            <li>4 weeks annual vacation</li>
            <li>Meaningful long-term compensation package</li>
            <li>Flexible and remote work environment</li>
        </ul>
    </Container>
  )
}

export default Careers3
