import React, {useState} from "react";
import styled from "styled-components";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import Navigate from '../../navigate';
import { faAngleUp, faAngleDown } from '@fortawesome/free-solid-svg-icons';

const Lists = styled.ul`
    list-style: none;
    display: flex;
    gap: 1.3em;
    font-family: 'Ruberoid';
    font-weight: 500;
    font-size: ${({scrolled}) => (scrolled ? '.9em' : '1.15em')};
    transition: all 0.6s ease-in-out;
`



function Navigation({scrolled}) {
    const [hover, setHover] = useState(null);


    function handleHover(index){
        setHover(index)
    }

    function handleNoHover(){
        setHover(null)
    }

    return (<nav>
        <Lists scrolled={scrolled}>
            {Navigate.map((nav, index) => {
            return(
                <NavList   index={index} handleHover={handleHover} handleNoHover={handleNoHover} hover={hover} faAngleUp={faAngleUp} faAngleDown={faAngleDown} scrolled={scrolled} sub={sub}  />)
})}
        </Lists>
    </nav>);
}

export default Navigation
