import React from 'react'
import styled from 'styled-components'
import HeaderBG from '../assets/HeaderBG.webp'
import HomepageBG from '../assets/HomepageBG.webp'
import Heading from './Heading'

const BlendedBackground = styled.div`
    width: 100%;
    height: 100vh;
    background-image: url(${HeaderBG}), url(${HomepageBG});
    background-size: cover, 140%;
    background-position: top, top;
    background-blend-mode: multiply;
`

const Title = styled.h1`

`

function LandingPage() {
  return (
    <div>
        <BlendedBackground>
            <Heading/>
            <Title>The first modular blockchain network</Title>
            <p>Celestia is a modular data availability network that makes it easy for anyone to securely launch their own blockchain.</p>
            <button>Build modular</button>
            <button>Explore</button>
        </BlendedBackground>
    </div>
  )
}

export default LandingPage
