import React, { useEffect, useState } from 'react';
import styled from 'styled-components';
import CelestiaLogo from '../assets/CelestiaLogo.svg';
import '../assets/fonts/stylesheet.css'
import Navigation from './Navigation/Navigation';
import { Link } from 'react-router-dom';

const Container = styled.header`
    display: flex; 
    position: fixed;
    gap: 2em;
    top: 0;
    left: 0;
    right: 0;
    z-index: 9999;
    align-items: center;
    justify-content: space-between;
    border: none;
    background-color: ${({scrolled}) => (scrolled ? 'rgba(255, 255, 255, 0.5)' : 'hsla(0,0%,100%,0)')};
    backdrop-filter: ${({scrolled}) => (scrolled ? 'blur(1em)' : 'none')}; 
    padding: ${({scrolled}) => (scrolled ? '1em 4.5em' : '2em 0')};
    transition: all 0.6s ease-in-out;

    img{
        width: ${(props) => (props.scrolled ? '50%' : '70%')};
        cursor: pointer;
        transition: transform 0.4s ease-in-out;
        transform: ${({scrolled}) => (scrolled ? 'scale(1.1, 1.1)' : 'scale(1, 1)')}
    }

    @media (min-width: 992px){
        padding: 3em;
    }
`

const Hamburger = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: space-around;
  background: #000;
  border: none;
  border-radius: 5px;
  height: 1em;
  width: 1em !important;
  padding: 8px 7px;
  margin-left: 1.2em;
  width: 15px;
  z-index: 101;
  cursor: pointer;

  @media (min-width: 992px){
    display: none;
  }

  div {
    width: 100%;
    height: 3px;
    background-color: #fff; 
    transform: rotate(0) translate(0);
    transition: cubic-bezier(0.175, 0.885, 0.32, 1.275) 0.5s;

    &:nth-child(1) {
      transform: ${({ openHamburger }) => openHamburger ? "rotate(45deg)" : 'rotate(0) translate(0)'};

    }

    &:nth-child(2) {
      transform: ${({ openHamburger }) => (openHamburger ? "scale(0.1)" : 'scale(1)')};
    }

    &:nth-child(3) {
      transform: ${({ openHamburger }) => (openHamburger ? "rotate(135deg)" : "rotate(0) translate(0)")};
    }
  }
`;

function Heading() {
    const [scrolled, setScrolled] = useState(false);
    const [openHamburger, setOpenHamburger] = useState(false);

    useEffect(() => {
        function handleScroll(){
            if(window.scrollY > 160){

                setScrolled(true)
            }else{
                setScrolled(false)
            }
        }

        window.addEventListener('scroll', handleScroll)

        return() => {
            window.removeEventListener('scroll', handleScroll)
        }
    }, [])

  return (
    <Container scrolled={scrolled}>
        <Hamburger onClick={() => {setOpenHamburger(val => !val)}} openHamburger={openHamburger}>
            <div/>
            <div/>
            <div/>
        </Hamburger>

        <Link to='/'>
            <img src={CelestiaLogo} alt='Celestia Logo' />
        </Link>
        <Navigation scrolled={scrolled} openHamburger={openHamburger}/>
    </Container>
  )
}

export default Heading
