import React, {useRef, useEffect} from 'react'
import Landing1 from './sub/Landing1'
import Landing2 from './sub/Landing2'
import Landing3 from './sub/Landing3'
import styled from 'styled-components'
import Landing4 from './sub/Landing4'
import Landing5 from './sub/Landing5'

const Container = styled.section`
  display: flex;
  flex-direction: column;
  gap: 7em;
`

function LandingPage() {

  useEffect(() => {
    window.scroll(0,0)
  })

  function handleExploreButton(){
    const target = document.getElementById('landing3')
    if (target){
      target.scrollIntoView({behavior: 'smooth', block:'start'})
    }
  }

  return (
    <Container>
        <Landing1 handleButton={handleExploreButton}/>
        <Landing2/>
        <Landing3 id='landing3'/>
        <Landing4/>
        <Landing5/>
    </Container>
  )
}

export default LandingPage
