import React, { useState } from 'react';
import styled from 'styled-components';
import CelestiaLogo from '../assets/CelestiaLogo.svg';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import '../assets/fonts/stylesheet.css'
import { faAngleUp, faAngleDown } from '@fortawesome/free-solid-svg-icons';

const Container = styled.header`
    display: flex; 
    padding: 2em 4.3em;
    align-items: center;
    justify-content: space-between;
`

const Lists = styled.ul`
    list-style: none;
    display: flex;
    gap: 1.3em;
    font-family: 'Ruberoid';
    font-weight: 500;
    font-size: 1.15em;
`

const List = styled.li`
    cursor: pointer;
`

const Arrow = styled(FontAwesomeIcon)`
    padding: 0;
`

function Heading() {

    const [hover, setHover] = useState(false)

  return (
    <Container>
        <img src={CelestiaLogo} alt='Celestia Logo'/>
        <nav>
            <Lists>
                <List>Learn <Arrow icon={faAngleDown}/></List>
                <List>Build <FontAwesomeIcon icon={faAngleDown}/></List>
                <List>Community <FontAwesomeIcon icon={faAngleDown}/></List>
            </Lists>
        </nav>
    </Container>
  )
}

export default Heading
