import { faCalendar, faLocation } from '@fortawesome/free-solid-svg-icons'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import React from 'react'
import styled from 'styled-components'
import Hackathon from '../assets/hackathonImage.webp'

const Container = styled.section`
    margin-top: 5em;
    padding-top: 1em;
    padding-left: 5em;

    h1{
        font-family: "Ruberoid";
        font-size: 7em;
        margin-bottom: 0;
        padding-bottom: 0;
    }
`

const SubTitle = styled.p`
    font-family: "Ruberoid";
    margin: 0;
    padding: 0;
    font-weight: 500;
    font-size: 1.2em;
`

const HackContainer = styled.div`
    display: flex;
    gap: 1em;
    width: 90%;
    align-items: center;
    background-color: #fff;
    border: 1px solid #e3e3e3;
    border-radius: 8px;
    box-shadow: 0 2px 2px rgba(0,0,0,.03);
    display: flex;
    flex-wrap: wrap;
    justify-content: space-between;
    padding: 8px;
    margin-top: 2em; 

    img{
        width: 50%;
        border-radius: 8px;
    }
`

function Events() {
  return (
    <Container>
        <h1>Events</h1>
        <SubTitle>Hangout with the Celestia community IRL or online.</SubTitle>

        <HackContainer>
            <img src={Hackathon} alt='Hackathon'/>
            <div>
                <div>
                    <h3>Celestia hackathon</h3>
                    <div>
                        <p><FontAwesomeIcon icon={faCalendar}/> Coming soon!</p>
                        <p><FontAwesomeIcon icon={faLocation}/> Online</p>
                    </div>
                </div>

                <button>View event</button>
            </div>
        </HackContainer>

        <div>
            <div>
                <h3>Community events</h3>
                <button>Submit your Event</button>
            </div>

            <div>View Past Events</div>
        </div>
    </Container>
  )
}

export default Events
